		var fixme  		= $('.fixed-div');
		var fixmeTop 	= 303;
		var stillTo 	= 303;
		$(window).scroll(function() {
		    var currentScroll = $(window).scrollTop();
		    if (currentScroll <= fixmeTop) {
		        $(".sub-nav").fadeOut();
		    } else {
		        $(".sub-nav").fadeIn();
		    }
		    if (currentScroll >= fixmeTop && currentScroll <= 2390) {
		        fixme.addClass("_sticky");
		    } else {
		        fixme.removeClass("_sticky");
		    }
		});

		function fnEnquiry(){
			var name 		= $("#name").val();
			var email  		= $("#email").val();
			var mobile 		= $("#mobile").val();
			var inputDate 	= $("#inputDate").val();
			var inputTime 	= $("#inputTime").val();

			var rNAme		= /^[a-zA-Z ]{2,30}$/;
			var rEmail 		= /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
			var rMobile		= /^(\+91-|\+91|0)?\d{10}$/;


			if (!rNAme.test(name) || name == "") {
				alert("Please enter valid name");
				$("#name").focus();
				return false;
			}

			if (!rEmail.test(email) || email == "") {
				alert("Please enter valid Email Id");
				$("#email").focus();
				return false;
			}

			if (!rMobile.test(mobile) || mobile == "") {
				alert("Please enter valid Mobile #");
				$("#mobile").focus();
				return false;
			}

			if (inputDate == "") {
				alert("Please Select convenient date");
				$("#inputDate").focus();
				return false;
			}

			if (inputTime == "") {
				alert("Please Select convenient time");
				$("#inputTime").focus();
				return false;
			}
			document.getElementById("enquiryForm").submit();
		}

		function onScroll(event){
		    var scrollPos = $(document).scrollTop();
		    $('.sub-nav  a').each(function () {
		        var currLink = $(this);
		        var refElement = $(currLink.attr("href"));
		        if (refElement.position().top <= scrollPos && refElement.position().top + refElement.height() > scrollPos) {
		            $('.sub-nav ul li').removeClass("_active");
		            currLink.parent("li").addClass("_active");
		        }
		        else{
		            currLink.parent("li").removeClass("_active");
		        }
		    });
		}
		
